--
-- Table structure for table `news_webpages`
--

DROP TABLE IF EXISTS `news_webpages`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `news_webpages` (
  `id` int NOT NULL AUTO_INCREMENT,
  `url` text COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '网页URL',
  `crawl_time` datetime DEFAULT NULL COMMENT '抓取时间',
  `visit_count` int DEFAULT '0' COMMENT '访问量',
  `news_website_id` int DEFAULT NULL COMMENT '所属网站ID',
  `data_source_id` int DEFAULT NULL COMMENT '数据源ID',
  `text_content_id` int DEFAULT NULL COMMENT '关联文本内容ID',
  PRIMARY KEY (`id`),
  UNIQUE KEY `text_content_id` (`text_content_id`),
  KEY `news_website_id` (`news_website_id`),
  KEY `data_source_id` (`data_source_id`),
  KEY `idx_crawl_time` (`crawl_time`),
  CONSTRAINT `news_webpages_ibfk_1` FOREIGN KEY (`news_website_id`) REFERENCES `news_websites` (`id`),
  CONSTRAINT `news_webpages_ibfk_2` FOREIGN KEY (`data_source_id`) REFERENCES `data_sources` (`id`),
